void XYZTPrintADCValues::Event()
{
   gAnalyzer->GetPMTData()->SetADC(gRandom->Gaus(50,10));
   GetADC()->Fill(gAnalyzer->GetPMTData()->GetADC());

   if (gAnalyzer->GetGSP()->GetOutputOnOff()) {
      cout << gAnalyzer->GetPMTData()->GetADC() << endl;
      cout << endl;
   }
}

